document.addEventListener("DOMContentLoaded",(()=>{!function(){const e=document.querySelector(".custom-select"),t=e.querySelector(".select-trigger"),c=e.querySelectorAll(".option"),n=e.querySelector("#selected-value");t.addEventListener("click",(()=>{e.classList.toggle("active")})),c.forEach((c=>{c.addEventListener("click",(()=>{t.textContent=c.textContent,n.value=c.dataset.value,e.classList.remove("active")}))})),document.addEventListener("click",(t=>{e.contains(t.target)||e.classList.remove("active")}))}()}));