function select(){const e=document.querySelector(".selected"),t=document.querySelector(".selected span"),n=document.querySelector(".options-container"),c=document.querySelectorAll(".option");e.addEventListener("click",(()=>{n.classList.toggle("active")})),c.forEach((e=>{e.addEventListener("click",(()=>{t.innerHTML=e.querySelector("label").innerHTML,n.classList.remove("active")}))}))}function range(){const e=document.getElementById("myRange"),t=document.querySelector(".range-text__value");t.innerHTML=e.value+"%",e.oninput=function(){t.innerHTML=this.value+"%"}}function burger(){const e=document.querySelector(".header__btn"),t=document.querySelector(".nav");e.addEventListener("click",(function(){this.classList.toggle("close"),t.classList.toggle("active")}))}window.addEventListener("load",(()=>{select(),range(),burger()}));